mapper.select:
SELECT COUNT(1)
from `processtask_step_task`
where `task_config_id` = #{value}

SELECT `id`,
       `processtask_step_task_id` as processTaskStepTaskId,
       `user_uuid`                as userUuid,
       `end_time`                 as endTime,
       `status`,
       `is_delete`                as isDelete
FROM `processtask_step_task_user`
where `processtask_step_task_id` = #{processTaskStepTaskId}
  and `user_uuid` = #{userUuid}
  and `id` = #{processTaskStepTaskUserId}

SELECT pstu.`id`, pstu.`processtask_step_task_id` as processTaskStepTaskId, pstu.`user_uuid` as userUuid, u.pinyin as pinyin, u.user_info as userInfo, u.vip_level as userLevel, u.user_name as userName, u.user_id as userId, pstu.`end_time` as endTime, pstu.`status`, pstu.`is_delete` as isDelete FROM `processtask_step_task_user` pstu left join `user` u on pstu.`user_uuid` = u.uuid where pstu.`processtask_step_task_id` in
#{stepTaskId}
and pstu.`user_uuid` = #{userUuid}

mapper.delete:
UPDATE `processtask_step_task_user`
SET
`is_delete` = #{isDelete}
WHERE `user_uuid`

    not

in

    #{userUuid}

and `processtask_step_task_id` = #{processTaskStepTaskId}
